export interface TimeRange {
    from?: string;
    to?: string;
}
export interface ModelFilter {
    includeDeleted?: boolean;
    createdRange?: TimeRange;
    updatedRange?: TimeRange;
}
export interface AccessKeyFilter extends ModelFilter {
    revokedRange?: TimeRange;
}
export interface AdminAccessKeyFilter extends AccessKeyFilter {
    xpubId?: string;
}
export type ContactStatus = 'unconfirmed' | 'awaiting' | 'confirmed' | 'rejected';
export interface ContactFilter extends ModelFilter {
    id?: string;
    fullName?: string;
    paymail?: string;
    pubKey?: string;
    status?: ContactStatus;
}
export interface DestinationFilter extends ModelFilter {
    lockingScript?: string;
    address?: string;
    draftId?: string;
}
export interface AdminPaymailFilter extends ModelFilter {
    id?: string;
    xpubId?: string;
    alias?: string;
    domain?: string;
    publicName?: string;
}
export type TransactionStatus = 'UNKNOWN' | 'QUEUED' | 'RECEIVED' | 'STORED' | 'ANNOUNCED_TO_NETWORK' | 'REQUESTED_BY_NETWORK' | 'SENT_TO_NETWORK' | 'ACCEPTED_BY_NETWORK' | 'SEEN_ON_NETWORK' | 'MINED' | 'SEEN_IN_ORPHAN_MEMPOOL' | 'CONFIRMED' | 'REJECTED';
export interface TransactionFilter extends ModelFilter {
    hex?: string;
    blockHash?: string;
    blockHeight?: number;
    fee?: number;
    numberOfInputs?: number;
    numberOfOutputs?: number;
    draftId?: string;
    totalValue?: number;
    status?: TransactionStatus | string;
}
export type UtxoType = 'pubkey' | 'pubkeyhash' | 'nulldata' | 'multisig' | 'nonstandard' | 'scripthash' | 'metanet' | 'token_stas' | 'token_sensible';
export interface UtxoFilter extends ModelFilter {
    transactionId?: string;
    outputIndex?: number;
    id?: string;
    satoshis?: number;
    scriptPubKey?: string;
    type?: UtxoType;
    draftId?: string;
    reservedRange?: TimeRange;
    spendingTxId?: string;
}
export interface AdminUtxoFilter extends UtxoFilter {
    xpubId?: string;
}
export interface XpubFilter extends ModelFilter {
    id?: string;
    currentBalance?: number;
}
